import{_ as r,a as t,af as a,o as i}from"./chunks/framework.C87LdZyP.js";const n="/assets/difference-between-ux-and-ui.Cw2_67dX.jpg",U=JSON.parse('{"title":"用户体验应该叫什么","description":"","frontmatter":{"title":"用户体验应该叫什么","date":"2021-05-08T00:00:00.000Z","tags":["ux"],"category":["UX"]},"headers":[],"relativePath":"posts/2021/05/2021-05-08-ux-ui.md","filePath":"posts/2021/05/2021-05-08-ux-ui.md","lastUpdated":1718173059000}'),s={name:"posts/2021/05/2021-05-08-ux-ui.md"};function o(u,e,d,l,c,p){return i(),t("div",null,e[0]||(e[0]=[a('<h2 id="ux-user-experience-ui-user-interface" tabindex="-1">UX (User Experience) / UI (User Interface) <a class="header-anchor" href="#ux-user-experience-ui-user-interface" aria-label="Permalink to &quot;UX (User Experience) / UI (User Interface)&quot;">​</a></h2><p>国外一篇文章介绍了两者的区别。</p><p><img src="'+n+'" alt=""></p><ul><li>用户体验设计: UX Design / User Experience Design</li><li>用户体验设计师: UX Designer / User Experience Designer</li></ul><p>有两个比喻来说明：</p><blockquote><p>“UI is the saddle, the stirrups, and the reins. UX is the feeling you get being able to ride the horse.”</p></blockquote><p>— Dain Miller, Web Developer</p><blockquote><p>“User Experience (UX) and User Interface (UI) are some of the most confused and misused terms in our field. A UI without UX is like a painter slapping paint onto a canvas without thought; while UX without UI is like the frame of a sculpture with no paper mache on it. A great product experience starts with UX followed by UI. Both are essential for the product’s success.”</p></blockquote><ul><li>Rahul Varshney, co-creator of Foster.fm</li></ul><h2 id="ued-user-experience-design-department" tabindex="-1">UED (User Experience Design Department) <a class="header-anchor" href="#ued-user-experience-design-department" aria-label="Permalink to &quot;UED (User Experience Design Department)&quot;">​</a></h2><p>据说这个称呼起源于Yahoo的用户体验部门，后来被阿里传播开来。</p><h2 id="ucd-user-centered-design" tabindex="-1">UCD (User Centered Design) <a class="header-anchor" href="#ucd-user-centered-design" aria-label="Permalink to &quot;UCD (User Centered Design)&quot;">​</a></h2><p>以用户为中心的设计。</p><h2 id="ugd-user-growth-design" tabindex="-1">UGD（User Growth Design） <a class="header-anchor" href="#ugd-user-growth-design" aria-label="Permalink to &quot;UGD（User Growth Design）&quot;">​</a></h2><p>用户增长设计。</p><h2 id="ixd-interaction-design" tabindex="-1">IxD (Interaction Design) <a class="header-anchor" href="#ixd-interaction-design" aria-label="Permalink to &quot;IxD (Interaction Design)&quot;">​</a></h2><p>交互设计</p><hr><ul><li><a href="https://careerfoundry.com/en/blog/ux-design/the-difference-between-ux-and-ui-design-a-laymans-guide/" target="_blank" rel="noreferrer">The Difference Between UX And UI Design - A Beginner&#39;s Guide</a></li><li><a href="https://www.zhihu.com/question/20380465" target="_blank" rel="noreferrer">UED/UX/UCD/UE 工作的内容及职位差别是什么</a></li></ul>',19)]))}const g=r(s,[["render",o]]);export{U as __pageData,g as default};
