import{_ as a,a as e,af as i,o as l}from"./chunks/framework.C87LdZyP.js";const u=JSON.parse('{"title":"某国移动一些技术方案思考","description":"","frontmatter":{"title":"某国移动一些技术方案思考","date":"2022-09-04T00:00:00.000Z","tags":["other"],"category":["other"]},"headers":[],"relativePath":"posts/2022/09/2022-09-04-software-architecture-thinking.md","filePath":"posts/2022/09/2022-09-04-software-architecture-thinking.md","lastUpdated":1718173059000}'),r={name:"posts/2022/09/2022-09-04-software-architecture-thinking.md"};function o(s,t,n,c,h,d){return l(),e("div",null,t[0]||(t[0]=[i('<p>偶然机会了解到某国移动公司对系统的要求。</p><h2 id="包瘦身" tabindex="-1">包瘦身 <a class="header-anchor" href="#包瘦身" aria-label="Permalink to &quot;包瘦身&quot;">​</a></h2><p>应用程序应尽可能小、尽可能快。</p><ul><li>包（镜像）体积变小，删除不必要的依赖</li><li>快速启动有要求</li><li>2c8G 是一个比较常见的容器资源限制</li></ul><h2 id="系统拆分" tabindex="-1">系统拆分 <a class="header-anchor" href="#系统拆分" aria-label="Permalink to &quot;系统拆分&quot;">​</a></h2><p>系统应该历史的拆分。</p><p>比如历史上某块儿用量比较大，比如波次，可以当初拆分出来。而不是理所当然的拆分，提到进销存，就要拆分出库、入库、库存。</p><h2 id="其他" tabindex="-1">其他 <a class="header-anchor" href="#其他" aria-label="Permalink to &quot;其他&quot;">​</a></h2><ul><li>用 xxl-job 的还挺多</li><li>服务基准性能也是客户比较关心的一个点 <ul><li>2c8g 什么样的业务，处理多少并发，多大TPS</li></ul></li><li>日志没特殊要求，一般加上 traceId 就可 <ul><li>线程池子线程、MQ消息等异步场景记得处理</li><li>必要时候加上业务ID（比如订单号）</li></ul></li></ul>',9)]))}const f=a(r,[["render",o]]);export{u as __pageData,f as default};
